mapper.select:
SELECT t.uid, t.id, t.document_number, t.title, t.approval_status, t.has_read, t.monitor_status, t.alert_node, t.department, t.applicant, t.project_name, t.belonging_org, t.budget_focal_point, t.budget_execution_type, t.budget_month, t.budget_total
,op1.operation_name,op1.by_operation_people_id
FROM
t_oa_marketing_apply t
(select Max(id) id,uid from t_oa_operation where by_operation_people_id =#{param.ldapId} group by uid) op inner join t_oa_operation op1 on op.id=op1.id inner join t_oa_marketing_apply t on op.uid=t.uid
t.id =#{param.id}
and t.document_number LIKE concat('%',#{param.documentNumber},'%')
and t.project_name LIKE concat('%',#{param.projectName},'%')
and t.belonging_org LIKE concat('%',#{param.belongingOrg},'%')
and t.budget_execution_type LIKE concat('%',#{param.budgetExecutionType},'%')
and t.alert_node =#{param.alertNode}
and t.has_read =#{param.hasRead}
and t.monitor_status =#{param.monitorStatus}
and t.apply_date LIKE concat('%',#{param.applyDate},'%')
and t.statistics_city_name =#{param.statisticsCityName}
order by update_time desc

select count(*) total, count(case when has_read = 'beread' then 1 end) beread, count(case when monitor_status = 'special_attention' then 1 end) specialAttention, count(case when monitor_status = 'warning' then 1 end) warning, count(case when monitor_status = 'normal' then 1 end) normal from ( SELECT t.id, t.document_number, t.project_name, t.belonging_org, t.budget_execution_type, t.apply_date, t.has_read, t.monitor_status, t.alert_node FROM
t_oa_marketing_apply t
(select Max(id) id,uid from t_oa_operation where by_operation_people_id =#{ldapId} group by uid) op inner join t_oa_operation op1 on op.id=op1.id inner join t_oa_marketing_apply t on op.uid=t.uid
) oa
oa.id =#{id}
and oa.document_number LIKE concat('%',#{documentNumber},'%')
and oa.project_name LIKE concat('%',#{projectName},'%')
and oa.belonging_org LIKE concat('%',#{belongingOrg},'%')
and oa.budget_execution_type LIKE concat('%',#{budgetExecutionType},'%')
and oa.alert_node=#{alertNode}
