mapper.select:
SELECT t.uid, t.id, t.document_number, t.title, t.apply_date, t.operator_name, t.organization, t.apply_dept, t.advance_number, t.contract_sort, t.purchase_sort_name, t.purchase_sort_number, t.bulletin_title, t.bulletin_number, t.purchase_organization, t.purchase_type, t.sign_up_efficient_time, t.remark, t.pre_trial_operator_name, t.supplier_head_tital, t.supplier_head_apply_dept, t.supplier_head_remark, t.supplier_head_organization, t.supplier_head_landline, t.supplier_head_operator_name, t.supplier_head_connect, t.supplier_result_remark, t.approval_status, t.has_read, t.monitor_status, t.alert_node, t.mixed_field, b.table_info
,op1.operation_name operation_node_name
FROM
t_oa_purchase_shortlisted t
(select Max(id) id,uid from t_oa_operation where by_operation_people_id =#{shortList.ldapId} group by uid) op inner join t_oa_operation op1 on op.id=op1.id inner join t_oa_purchase_shortlisted t on op.uid=t.uid
left join t_oa_active_form b on t.uid=b.uid and b.table_name = '资格入围结果'
t.id =#{shortList.id}
and t.document_number LIKE concat('%',#{shortList.documentNumber},'%')
and t.organization LIKE concat('%',#{shortList.organization},'%')
and t.apply_date LIKE concat('%',#{shortList.applyDate},'%')
and t.operator_name LIKE concat('%',#{shortList.applyName},'%')
and t.alert_node =#{shortList.alertNode}
and t.has_read =#{shortList.hasRead}
and t.monitor_status =#{shortList.monitorStatus}
and t.statistics_city_name =#{shortList.statisticsCityName}
and t.bidding_name =#{shortList.biddingName}
and t.company_name =#{shortList.forCompany}
and b.table_info LIKE concat('%',#{shortList.companyName},'%')
and t.purchase_sort_name LIKE concat('%',#{shortList.purchaseSortName},'%')
and t.purchase_organization LIKE concat('%',#{shortList.purchaseOrganization},'%')
and DATE_FORMAT(t.apply_date, '%Y-%m-%d') >= DATE_FORMAT(#{shortList.startTime}, '%Y-%m-%d')
and DATE_FORMAT(t.apply_date, '%Y-%m-%d') <= DATE_FORMAT(#{shortList.endTime}, '%Y-%m-%d')
and t.approval_status = #{shortList.approvalStatus}
order by t.update_time desc

select count(*) total, count( CASE WHEN t.has_read = 'beread' THEN 1 END ) beread, count( CASE WHEN t.monitor_status = 'special_attention' THEN 1 END ) specialAttention, count( CASE WHEN t.monitor_status = 'warning' THEN 1 END ) warning, count( CASE WHEN t.monitor_status = 'normal' THEN 1 END ) normal from
t_oa_purchase_shortlisted t
(select Max(id) id,uid from t_oa_operation where by_operation_people_id =#{shortList.ldapId} group by uid) op inner join t_oa_purchase_shortlisted t on op.uid=t.uid
t.id =#{shortList.id}
and t.document_number LIKE concat('%',#{shortList.documentNumber},'%')
and t.organization LIKE concat('%',#{shortList.organization},'%')
and t.apply_date LIKE concat('%',#{shortList.applyDate},'%')
and t.operator_name LIKE concat('%',#{shortList.applyName},'%')
and t.alert_node =#{shortList.alertNode}
and t.has_read =#{shortList.hasRead}
and t.monitor_status =#{shortList.monitorStatus}
