mapper.select:
select count(*) total, count(case when has_read='beread'then 1 end) beread, count(case when monitor_status='special_attention'then 1 end) specialAttention, count(case when monitor_status='warning'then 1 end) warning, count(case when monitor_status='normal'then 1 end) normal from( SELECT t.id, t.document_number, t.apply_date, t.city_firm, t.operator_name, t.has_read, t.monitor_status, t.alert_node, t.original_dep, t.initiate_dep, t.owned_purchas_line, t.purchasing_type, t.procurement_model FROM
t_oa_purchase_singlesource t
(select Max(id) id,uid from t_oa_operation where by_operation_people_id =#{ldapId} group by uid) op inner join t_oa_operation op1 on op.id=op1.id inner join t_oa_purchase_singlesource t on op.uid=t.uid
) oa
oa.id =#{id}
and oa.initiate_dep LIKE concat('%',#{initiateDep},'%')
and oa.owned_purchas_line like "%" #{ownedPurchasLine} "%"
and oa.purchasing_type like "%" #{purchasingType} "%"
and oa.procurement_model like "%" #{procurementModel} "%"
and oa.alert_node=#{alertNode}
